mapper.select:
select gender ,count(gender) as counts from repurchase inner JOIN base_user_tag on base_user_tag.user_id = repurchase.id where repurchase = #{repurchase}
and address like concat('%', #{address}, '%')
GROUP BY base_user_tag.gender;

select age ,count(age) as counts from repurchase inner JOIN base_user_tag on base_user_tag.user_id = repurchase.id where repurchase = #{repurchase}
and address like concat('%', #{address}, '%')
GROUP BY base_user_tag.age;

select user_id AS actType,count(user_id) as counts from repurchase INNER JOIN base_user_tag on base_user_tag.user_id = repurchase.id where repurchase = #{repurchase}
and address like concat('%', #{address}, '%')
GROUP BY user_id ORDER BY counts desc limit 5;

select repurchase as actType,count(repurchase) as counts from repurchase INNER JOIN base_user_tag on base_user_tag.user_id = repurchase.id
address like concat('%', #{address}, '%')
GROUP BY repurchase ORDER BY counts desc;
