mapper.select:
select * from rfm



            recency = #{Recency}


            and frequency = #{Frequency}


            and consumption_capacity = #{Monetary}

select rfm_tag AS actType ,count(rfm_tag) as counts from repurchase INNER JOIN rfm on rfm.user_id = repurchase.id INNER join base_user_tag on base_user_tag.user_id = repurchase.id where repurchase = #{repurchase}
and address like concat('%', #{address}, '%')
GROUP BY rfm.rfm_tag;

SELECT user_id,consumption_capacity_num,recency_num,frequency_num from rfm WHERE rfm_tag = #{rfmTag} and time = #{time}
and address LIKE concat('%', #{address}, '%')
limit 1000

SELECT rfm_tag,avg(consumption_capacity_num) consumption_avg,avg(recency_num) recency_avg,avg(frequency_num) frequency_avg from rfm WHERE rfm_tag in
#{item}
and time = #{time}
and address LIKE concat('%', #{address}, '%')
GROUP BY rfm_tag

SELECT rfm_tag actType,count(user_id) counts from rfm WHERE time = #{time}
and address LIKE concat('%', #{address}, '%')
GROUP BY rfm_tag

        select * from rfm where  rfm_tag = #{rfmTag} and time =#{time} 
           and   address  like  concat('%', #{address}, '%')

limit 150;

SELECT recency_num,frequency_num , AVG(consumption_capacity_num) consumption_avg from rfm WHERE rfm_tag = #{rfmTag} and time = #{time}


    and   address  like  concat('%', #{address}, '%')

GROUP BY recency_num,frequency_num ORDER BY consumption_avg
