mapper.select:
select * from rfm



            recency = #{Recency}


            and frequency = #{Frequency}


            and consumption_capacity = #{Monetary}

select rfm_tag AS actType ,count(rfm_tag) as counts from repurchase INNER JOIN rfm on rfm.user_id = repurchase.id INNER join base_user_tag on base_user_tag.user_id = repurchase.id where repurchase = #{repurchase}
and address like concat('%', #{address}, '%')
GROUP BY rfm.rfm_tag;
